import React, {Component} from 'react';
import ListOfPosts from './ListOfPosts'

class App extends Component {
  constructor(props){
    super(props)

    this.state = {
      searchInput : '',
      posts : [],
      isLoading : 'True',
      loadCount : 10
      // url : ""
    }

    this.componentDidMount = this.componentDidMount.bind(this)
    this.getPosts = this.getPosts.bind(this)
    this.handleScroll = this.handleScroll.bind(this)
    this.handleKeypress = this.handleKeypress.bind(this);
    this.getPostsQuery = this.getPostsQuery.bind(this)
    // this.handleClick = this.handleClick.bind(this)
  }

  loadTenPosts(){
    this.setState({
      loadCount : this.state.loadCount + 10
    })
    if(this.state.searchInput === ''){
      this.getPosts();
    }else{
      this.getPostsQuery();
    }
  }

  handleScroll = (e) => {
    if(Math.ceil(window.innerHeight + window.scrollY) >= document.documentElement.scrollHeight){
      this.loadTenPosts();
    }
  }

  handleKeypress(e){
    if(e.key === 'Enter' && e.target.value !== ''){
      this.setState({
        searchInput : e.target.value
      })
      this.getPostsQuery(e.target.value)
    }
  }

  // handleClick(url){
  //   this.setState({
  //     url : url
  //   })
  // }

  componentDidMount(){
    this.setState({
      isLoading : 'False'
    })
    this.getPosts();
    window.addEventListener('scroll', this.handleScroll);
  }

  async getPosts(){
    const result = await fetch(`http://localhost:8000/api/articles`);
    const resultObject = await result.json();
    this.setState({
      posts : resultObject,
    })
  }

  async getPostsQuery(query){
    const result = await fetch(`http://localhost:8000/api/articles/${query}`);
    const resultObject = await result.json();
    this.setState({
      posts : resultObject,
    })
  }

  render(){
    return(
      <div id='container'>
        <div id='topBar'>
          <div id='loginContainer' hidden='!'>
            <input id='username' placeholder='Username'></input>
            <input id='password' placeholder='Password' type='password'></input>
            <button id='createAccount' type="submit">Create Account</button>
            <button id='login' type="submit">Login</button>
          </div>
          <input id='searchbar' placeholder='Search for Keywords (eg. Nasa, SpaceX, Mars)' onKeyPress={this.handleKeypress}></input>
        </div>
        <div id='feedContainer' onScroll={this.handleScroll}>
          <ListOfPosts 
            posts={this.state.posts}
            loadCount={this.state.loadCount}
            handleClick={this.handleClick}
          /> 
        </div>
      </div>
    )
  }
}

export default App;
