const Post = (props) => {
    const articleSummary = props.post.articleSummary;
    const datePublished = props.post.datePublished;
    const sitePublishedOn = props.post.sitePublishedOn;
    return(
        <div class='postContainer'>
            <div class='postTextContainer'>
                <p class='newsArticle'>{articleSummary}</p>
                <span class='publishDate'>{datePublished}</span>
                <span class='postSite'>{sitePublishedOn}</span>
            </div>
        </div>
    )
}

export default Post;
