import React, {Component} from 'react';
import ListOfPosts from './ListOfPosts'

class App extends Component {
  constructor(props){
    super(props)

    this.state = {
      searchInput : null,
      posts : [],
      isLoading : 'True',
      loadCount : 10
    }

    this.componentDidMount = this.componentDidMount.bind(this)
    this.getPosts = this.getPosts.bind(this)
    this.handleScroll = this.handleScroll.bind(this)
  }

  loadTenPosts(){
    console.log('AHHHHHHHHHHHHHH')
    this.setState({
      loadCount : 20
    })
    this.getPosts();
  }

  handleScroll = (e) => {
    //console.log(this.scrollRef)
    if(e.scrollHeight - Math.abs(e.scrollTop) === e.clientHeight){
      console.log('HEY')
      this.loadTenPosts();
    }
  }

  componentDidMount(){
    this.setState({
      isLoading : 'False'
    })
    this.getPosts();
    window.addEventListener('scroll', this.handleScroll);
  }

  async getPosts(dataToRequest){
    console.log('Getting Posts...')
    const result = await fetch(`http://localhost:8000/api/articles`);
    const resultObject = await result.json();
    this.setState({
      posts : resultObject,
    })
  }

  render(){
    return(
      <div id='container'>
        <div id='topBar'>
          <h1 id='appTitle'>Go For Space</h1>
        </div>
        <div id='feedContainer' onScroll={this.handleScroll}>
          <ListOfPosts 
            posts={this.state.posts}
            loadCount={this.state.loadCount}
          /> 
        </div>
      </div>
    )
  }
}

export default App;
