import React, {Component} from 'react';
import ListOfPosts from './ListOfPosts'

class App extends Component {
  constructor(props){
    super(props)

    this.state = {
      searchInput : null,
      posts : [],
      isLoading : 'True',
      loadCount : 10
    }

    this.componentDidMount = this.componentDidMount.bind(this)
    this.getPosts = this.getPosts.bind(this)
    this.handleScroll = this.handleScroll.bind(this)
  }

  loadTenPosts(){
    this.setState({
      loadCount : this.state.loadCount + 10
    })
    this.getPosts();
  }

  handleScroll = (e) => {
    if(Math.ceil(window.innerHeight + window.scrollY) >= document.documentElement.scrollHeight){
      this.loadTenPosts();
    }
  }

  handleKeypress = (e) => {
    if(e.keyCode === 13){
      console.log(enter)
    }
  }

  componentDidMount(){
    this.setState({
      isLoading : 'False'
    })
    this.getPosts();
    window.addEventListener('scroll', this.handleScroll);
  }

  async getPosts(dataToRequest){
    const result = await fetch(`http://localhost:8000/api/articles`);
    const resultObject = await result.json();
    this.setState({
      posts : resultObject,
    })
  }

